import { Input, Form, FormGroup, Label, Button, FormText, Card, Container, InputGroup } from "reactstrap";
import { useState } from "react";
import { Profile } from "./Profile";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";
import { CardBody, CardHeader, Col, Row,FormFeedback } from 'reactstrap';
import '../App.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {  faAddressCard, faAppleAlt, faArrowLeft, faArrowRight, faArrowRightArrowLeft, faCheck, faCheckSquare, faCircle, faCircleUser, faCode, faCoffee, faEnvelope, faEnvelopeCircleCheck, faEnvelopeSquare, faHouseFloodWaterCircleArrowRight, faKey, faMapLocation, faMapMarked, faMapMarker, faMapMarkerAlt, faMapPin, fas, faSadTear, faSchool, faUser, faUserAltSlash, faUserCircle } from '@fortawesome/free-solid-svg-icons'
import  {faUser as faUser1} from '@fortawesome/free-regular-svg-icons/faUser' 
import { Icon } from "@fortawesome/fontawesome-svg-core";
import {faUser as faUser2 } from "@fortawesome/free-solid-svg-icons/faUser";
import { faMap } from "@fortawesome/free-regular-svg-icons";

const queryParams = new URLSearchParams(window.location.search)




// //$('input[name="from"]').val('');
// //$('input[name="from"]').attr("placeholder","Check In");
// var Date = document.getElementById('IdDate');
// Date.setAttribute("placeholder","Check In")




export function FormComponent(params) {
  const [displayPart1, setdisplay1] = useState("d-fl");
  const [displayPart2, setdisplay2] = useState("d-none");
  const [emailStatusvalid, setemailStatusvalid] = useState(false);
  const [emailStatusinvalid, setemailStatus] = useState(true);
  const [emailicon, setemailicon] = useState(<FontAwesomeIcon icon={faEnvelope}/>);
  const [display, setdisplay] = useState('m-auto');
  const [ImgFile, setImgFile] = useState('');


  // useEffect(() => {
  //   effect
    
  // });


  const Next = () => () => {
    if (emailStatus) {
      setdisplay1('d-none'); setdisplay2('d-block')
    }
    else {
      alert("Invalid Email")
    }
  }

  function EmailValidation(event) {
    // setemailStatus(event.target.checkValidity())
    // setemail(event.target.value)
    // console.log(email + "  " + emailStatus)
    event.target.setAttribute("valid", true);
    
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(event.target.value))
    {
      setemailicon(<></>)
      setemailStatus(true)
      return (true)
    }
      //alert("addresse email n'est pas valide!")
      setemailicon(<></>)
      setemailStatus(false)
      return (false)
  }


  function NomPrenomValidation(a,message) {
    
    if (/^(?!.*  )(?=.*[\w-])[\w -]{1,10}$/.test(a.target.value))
    {
      setemailStatus(true)
      return (true)
    }
      alert(message)
      setemailStatus(false)
      return (false)
  }



  function DateValidation(a) {
      if (/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/.test(a.target.value))
    {
      //DD/MM/YYYY
      setemailStatus(true)
      return (true)
    }
      alert("Date de naissance n'est pas valide!")
      setemailStatus(false)
      return (false)
  }


console.log(emailStatus)

  function getEvent(event) {
    setImgFile(JSON.stringify(event.target.files[0]))
console.log(ImgFile)
  }

  return (

<div className="card " id='formCard'>
  
          <div className="card-header" >
          Inscription
         </div>
         <div className="card-body">
           <Container>
             <Form method="get" onSubmit={()=>{setdisplay("m-auto d-none")}} action="/profile" className={display} >
          <FormGroup className={displayPart1}  >
            <FormGroup className='my-3' >
           
            <div className="position-relative">
<Input    name="Nom"  type="text"   placeholder="Nom" />
<span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faUser1}/></span>
 </div>
  </FormGroup>

           

<FormGroup className='my-3'> 
<div className="position-relative">
<Input    name="Prenom"  type="text"   placeholder="Prénom" />
<span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faUser2}/></span>
 </div>
 </FormGroup>


            <FormGroup className='my-3'>
              <Input
                id="exampleSelect"
                name="Sex"
                type="select"
              >
                <option hidden value="" defaultValue={'disabled'} >Sex</option>
                <option>
                  Féminin
                </option>
                <option>
                  Masculin
                </option>
              </Input>
              
            </FormGroup>
            <FormGroup className='mb-4' style={{margin :'25px 0'}}>
            <div className="position-relative">

              <Input
                id="email"
                name="email"
                placeholder="Email"
                type="text"
                onChange={data => EmailValidation(data)}
                invalid={!emailStatusinvalid}
                valid={emailStatusvalid}
                
              />
              <FormFeedback tooltip  style={{fontSize:'12px'}}>
      Oh noes! that name is already taken
    </FormFeedback>
<span style={{position:"absolute",right:"8px",top:'5px'}}>{emailicon}</span>

 </div>
            </FormGroup>
           
            <FormGroup className='my-3' >
            <div className="position-relative">

              <Input
                id="examplePassword"
                name="password"
                placeholder="Mot de passe"
                type="password"
              />
              <span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faKey}/></span>
 </div>
            </FormGroup>

