         <div className="card-body">
           <Container>
             <Form method="get" onSubmit={()=>{setdisplay("m-auto d-none")}} action="/profile" className={display} >
          <FormGroup className={displayPart1}  >
            <FormGroup className='my-3' >
           
            <div className="position-relative">
<Input    
name="Nom"  
type="text"   
placeholder="Nom" 
onChange={data => NomPrenomPasswordValidation(data)}
invalid={NomStatusinvalid}
valid={NomStatusvalid}/>

<span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faUser1}/></span>
 </div>
  </FormGroup>

           

<FormGroup className='my-3'> 
<div className="position-relative">
<Input    name="Prenom"  type="text"   placeholder="Prénom" />
<span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faUser2}/></span>
 </div>
 </FormGroup>


            <FormGroup className='my-3'>
              <Input
                id="exampleSelect"
                name="Sex"
                type="select"
              >
                <option hidden value="" defaultValue={'disabled'} >Sex</option>
                <option>
                  Féminin
                </option>
                <option>
                  Masculin
                </option>
              </Input>
              
            </FormGroup>
            <FormGroup className='mb-4' style={{margin :'25px 0'}}>
            <div className="position-relative">

              <Input
                id="email"
                name="email"
                placeholder="Email"
                type="text"
                onChange={data => EmailValidation(data)}
                invalid={emailStatusinvalid}
                valid={emailStatusvalid}
              />
              
<span style={{position:"absolute",right:"8px",top:'5px'}}>{emailicon}</span>

 </div>
            </FormGroup>
           
            <FormGroup className='my-3' >
            <div className="position-relative">

              <Input
                id="examplePassword"
                name="password"
                placeholder="Mot de passe"
                type="password"
              />
              <span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faKey}/></span>
 </div>
            </FormGroup>

            <Button color="primary" onClick={Next()} style={{position:'absolute',top:'5px',right:'5px'}}>
            <FontAwesomeIcon icon={faArrowRight}/>
            </Button>
          </FormGroup>



          <FormGroup className={displayPart2} >
            <FormGroup className="my-3">
            <div className="position-relative">

    <Input
      id="exampleText"
      name="Adresse"
      placeholder="Adresse"
      type="textarea"
    />
     <span style={{position:"absolute",right:"8px",top:'5px'}}  ><FontAwesomeIcon icon={faMapMarkerAlt}/></span>
 </div>
  </FormGroup>
  <FormGroup floating className="my-3">
      <Input
        id="exampleEmail"
        name="DateNaissance"
        placeholder="Email"
        type="date"
      />
      <Label for="exampleEmail" style={{fontSize:'15px'}}>
      Date de naissance
      </Label>
    </FormGroup>
            
            <FormGroup className="my-3" >
              <Col>
              <Label style={{fontWeight:"bold"}}>
                Hobbies
              </Label>
              </Col>
              
              <FormGroup check  inline>
                <Input type="checkbox" name="Sport" value={'Sport'}   />
                <Label check style={{fontSize:'17px'}} >
                  Sport
                </Label>
              </FormGroup >
              <FormGroup check inline>
                <Input type="checkbox" name="Ecriture" value={'Ecriture'}/>
                <Label check style={{fontSize:'17px'}}>
                  Ecriture
                </Label>
              </FormGroup>
              <FormGroup check inline>
                <Input type="checkbox" name="Voyage" value={'Voyage'}/>
                <Label check style={{fontSize:'17px'}}>
                  Voyage
                </Label>
              </FormGroup>
            </FormGroup>
            <Button onClick={() => { setdisplay2('d-none'); setdisplay1('d-block') }} style={{position:'absolute',top:'5px',left:'5px'}}>
            <FontAwesomeIcon icon={faArrowLeft}/>
            </Button>
            <Button color="success" type="submit" style={{position:'absolute',top:'5px',right:'5px'}}>
            <FontAwesomeIcon icon={faCheck}/> 
            </Button>
          </FormGroup>
        </Form>
      </Container>

</div>
</div>


  );

}
